import React, { useState } from "react"
import axios from "axios"
import "b"

function App() {

  const [url, setUrl] = useState('');
  const [barcodeImage, setBarcodeImage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/generateBarcode', { url });
      setBarcodeImage(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
    <h1 className="text-center" >URL to Barcode Converter</h1>
    <form  onSubmit={handleSubmit}>

    <div className="mb-3">
      <input
        type="text"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        placeholder="Enter URL..."
      />
      </div>




      <button  type="submit">Generate Barcode</button>
    </form>
    {barcodeImage && <img src={`data:image/png;base64,${barcodeImage}`} alt="Barcode" />}
  </div>
  )

}

export default App;
