import React, { useState } from "react"
import axios from "axios"
import "bootstrap/dist/css/bootstrap.min.css"

function App() {

  const [url, setUrl] = useState('');
  const [barcodeImage, setBarcodeImage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/generateBarcode', { url });
      setBarcodeImage(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
   <div className="container text-center">
   <div className="row">
   <div className="col">

   <h1>URL to Barcode Converter</h1>
    <form  onSubmit={handleSubmit}>

    <div className="mb-3">
      <input
        type="text"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        placeholder="Enter URL..."
      />
      </div>

      <button className="btn btn-primary" type="submit">Generate Barcode</button>
    </form>


    {barcodeImage && <img src={`data:image/png;base64,${barcodeImage}`} alt="Barcode" />}
   </div>

   </div>

   </div>
  </div>
  )

}

export default App;
