import React, { useState } from "react"
import axios from "axios"


function App() {

  const [url, setUrl] = useState('');
  const [barcodeImage, setBarcodeImage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/generateBarcode', { url });
      setBarcodeImage(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
    <h1 className="text-center" >URL to Barcode Converter</h1>
    <form  onSubmit={handleSubmit}>
      <input
        type="text"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        placeholder="Enter URL..."
      />

<div className="mb-3">
    <label for="exampleInputEmail1" className="form-label">Email address</label>
    <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/>
    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
  </div>



      <button type="submit">Generate Barcode</button>
    </form>
    {barcodeImage && <img src={`data:image/png;base64,${barcodeImage}`} alt="Barcode" />}
  </div>
  )

}

export default App;
